'use strict';

import React, {Component}  from 'react';
import {  ImageBackground,
View, Text, TextInput, TouchableHighlight, Picker} from 'react-native';
import ValidationComponent from 'react-native-form-validator';
import {Dimensions} from 'react-native';
import {Image, StyleSheet} from 'react-native';
import Icon from 'react-native-vector-icons/Ionicons';

const dimensions = Dimensions.get('window');
const imageHeight = dimensions.height ;
const imageWidth = dimensions.width;


export default class FormTest extends ValidationComponent {

  constructor(props) {
    super(props);
    this.state = {name : "", email: "", number:"56", date: "", newPassword : "", confirmPassword : "",PickerValueHolder: [],
    selectedValue: ''};
this._onPressButton = this._onPressButton.bind(this);
  }

  componentDidMount() {
	  let data = {
  method: 'POST',
  credentials: 'same-origin',
  mode: 'same-origin',
  body: JSON.stringify({
    appoid: 1
  }),
  headers: {
    'Accept':       'application/json',
    'Content-Type': 'application/json'
  }
}
    // remove the return 
   fetch('http://localhost/paykitaz-merchant-api/api/paket/businesstype').then((response) => response.json())
      .then((responseJson) => {
        // use the inventoryTypeData as it is already an array
        let PickerValueHolder = responseJson.data;
        this.setState({ PickerValueHolder }); // Set the new state
      })
      .catch((error) => {
        console.error(error);
      });
    }

  GetPickerSelectedItemValue=()=>{
    Alert.alert(this.state.PickerValueHolder);
  }
  _onPressButton() {
    // Call ValidationComponent validate method
    this.validate({
      name: {minlength:3, maxlength:7, required: true},
      email: {email: true},
      number: {numbers: true},
      date: {date: 'YYYY-MM-DD'},
	  confirmPassword : {equalPassword : this.state.newPassword}
	  });
  }

  render() {
      return (
    <ImageBackground
      style={styles.background}
      source={require("../assets/register_1_bg.png")}
    >
      <View style={styles.toptitle}>
        <Text style={styles.h1}>Daftar Paykitaz Merchant</Text>
      </View>
        <View style={styles.formreg1}>
	  <View style={{flexDirection:'row', justifyContent:'flex-end'}}>
	<img
      src={require('../assets/toko_logo.png')} width={"80"}
    />
  <Text style={styles.h1_black}>Profil Merchant</Text>
	  </View>
		<Text style={styles.small}>Beritahu Kami tentang Merchant mu.</Text>
			<Text>
            Nama Merchant
          </Text>
  <TextInput ref="name" style={styles.inputarea} onChangeText={(name) => this.setState({name})} value={this.state.name} />
			<Text>
            Bidang Bisnis
          </Text>
<View style={styles.container}>
        {<Picker
                selectedValue={this.state.selectedValue}
                onValueChange={(itemValue, itemIndex) => this.setState({selectedValue: itemValue})} >
                { this.state.PickerValueHolder.map((item, key)=>
                  <Picker.Item label={item.name} value={item.name} key={key} />
                )}
              </Picker>}
      </View>
	  <TextInput ref="name" style={styles.inputarea} onChangeText={(name) => this.setState({name})} value={this.state.name} />
          <TextInput ref="email" onChangeText={(email) => this.setState({email})} value={this.state.email} />
          <TextInput ref="number" onChangeText={(number) => this.setState({number})} value={this.state.number} />
          <TextInput ref="date" onChangeText={(date) => this.setState({date})} value={this.state.date} />
          {this.isFieldInError('date') && this.getErrorsInField('date').map(errorMessage => <Text>{errorMessage}</Text>) }

          <TextInput ref="newPassword" onChangeText={(newPassword) => this.setState({newPassword})} value={this.state.newPassword}  secureTextEntry={true}/>
          <TextInput ref="confirmPassword" onChangeText={(confirmPassword) => this.setState({confirmPassword})} value={this.state.confirmPassword} secureTextEntry={true} />
          {this.isFieldInError('confirmPassword') && this.getErrorsInField('confirmPassword').map(errorMessage => <Text>{errorMessage}</Text>) }

          <TouchableHighlight onPress={this._onPressButton}>
            <Text>Submit</Text>
          </TouchableHighlight>

          <Text>
            {this.getErrorMessages()}
          </Text>
        </View>
	</ImageBackground>
      );
  }

}

const styles = StyleSheet.create({
  background: {
    flex: 1,
    justifyContent: "flex-start",
    alignItems: "center",
    width: "100%",
  },
  inputarea: {
    width: (imageWidth-100),
	height: 20,
	fontSize:14,
	borderBottomWidth:2,
	paddingBottom:20,
  },
  h1:{
	  fontSize:40,
	  color:"#ffffff",
	  padding:20,
  },
  
  h1_black:{
	  fontSize:30,
	  color:"#000000",
	  padding:10,
  },
  small:
  {
	  fontSize:14,
	  color:"#000000",
	  padding:3,
	  
  },
  formreg1:
  {
	  marginTop:80,
	  padding:20,
	  borderRadius:22,
	  borderWidth:2,
	  borderColor: "#e8e4e3",
	  backgroundColor:"#ffffff",
	  width: (imageWidth-40),
	  fontFamily: "Verdana",
	  
  }
});
