import React from "react";
import {
  StyleSheet,
  Text,
  View,
  ImageBackground,
  Image,
} from "react-native";
import AppButton from "../components/appbutton";
 
const LoginRegisterScreen = ({navigation}) => {
  return (
    <ImageBackground
      style={styles.background}
      source={require("../assets/register_2_bg.png")}
    >
      <View style={styles.logoContainer}>
        <Text style={styles.big}>Selamat Datang di Paykitaz Merchant</Text>
        <Text style={styles.small}>Semua alat untuk mengelola dan mengembangkan bisnis anda</Text>
      </View>
      <View style={styles.buttonbox}>
        <AppButton  title={"Daftar"} size="sm" color="#64c098"  onPress={() => navigation.navigate('Register')} />
        
      </View>
      <View style={styles.buttonbox2}>
        <AppButton  title={"Masuk"} size="sm"  color="blue" onPress={() => navigation.navigate('Login')}/>
        
      </View>
    </ImageBackground>
  );
};

export default LoginRegisterScreen;
const styles = StyleSheet.create({
  background: {
    flex: 1,
    justifyContent: "flex-end",
    alignItems: "center",
    width: "100%",
  },
buttonbox: {
	marginRight:40,
    marginLeft:40,
	marginTop:10,
    paddingLeft:10,
    paddingRight:10,
    paddingTop:10,
	width:240,
    paddingBottom:10,
    backgroundColor:'#64c098',
    borderRadius:22,
    borderWidth: 1,
    borderColor: '#fff'
},
buttonbox2: {
	marginRight:40,
    marginLeft:40,
	marginTop:10,
    paddingLeft:10,
    paddingRight:10,
    paddingTop:10,
	width:240,
    paddingBottom:10,
    backgroundColor:'blue',
    borderRadius:22,
    borderWidth: 1,
    borderColor: '#fff'
},
  logo: {
    width: 100,
    height: 100,
    backgroundColor: "white",
    borderRadius: 250,
  },
  big: {
    fontSize: 36,
    justifyContent: "center",
    color: "black",
    alignItems: "center",
	marginTop:210,
	padding:20,
  },
  small: {
    fontSize: 20,
    justifyContent: "center",
    color: "black",
    alignItems: "center",
	padding:20,
  }
  ,  logoContainer: {
    top: 70,
    position: "absolute",
    alignItems: "center",
  },
  masuk:
  {
  }
  ,
});
