import React from 'react'
import { render } from 'react-dom'
import Styles from './Styles'
import { Form } from 'react-final-form'
import { Field } from 'react-final-form-html5-validation'
import {
  StyleSheet,
  Text,
  View,
  ImageBackground,
  Image,
} from "react-native";

import * as Keychain from 'react-native-keychain';

import $ from 'jquery';

	function submit()
	{
	$.ajax({
		url: 'http://localhost/paykitaz-merchant-api/api/paket/province',
		data: "test=test",
		cache: false,
		contentType: false,
		processData: false,
		method: 'POST',
		type: 'POST', // For jQuery < 1.9
		success: function(data){
			//alert(data.datax[0].bt_name);
			var i;
			for(i=0;i<data.datax.length;i++)
			{
				//alert(data.datax[i].bt_name);
						$("#province").append(new Option(data.datax[i].name, data.datax[i].id));

			}
		}
	});
	return false;
	}
	function submit2(id)
	{
	$.ajax({
		url: 'http://localhost/paykitaz-merchant-api/api/paket/kabupaten',
		data: "province_id=" + id,
		cache: false,
		contentType: false,
		processData: false,
		method: 'POST',
		type: 'POST', // For jQuery < 1.9
		success: function(data){
			//alert(data.datax[0].bt_name);
			var i;
			for(i=0;i<data.datax.length;i++)
			{
				//alert(data.datax[i].bt_name);
				$("#kabupaten").append(new Option(data.datax[i].name, data.datax[i].id));

			}
		}
	});
	return false;
	}
	function submit3(id)
	{
	$.ajax({
		url: 'http://localhost/paykitaz-merchant-api/api/paket/kecamatan',
		data: "regency_id=" + id,
		cache: false,
		contentType: false,
		processData: false,
		method: 'POST',
		type: 'POST', // For jQuery < 1.9
		success: function(data){
			//alert(data.datax[0].bt_name);
			var i;
			for(i=0;i<data.datax.length;i++)
			{
				//alert(data.datax[i].bt_name);
						$("#province").append(new Option(data.datax[i].name, data.datax[i].id));

			}
		}
	});
	return false;
	}
	function submit4(id)
	{
	$.ajax({
		url: 'http://localhost/paykitaz-merchant-api/api/paket/kelurahan',
		data: "district_id=" + id,
		cache: false,
		contentType: false,
		processData: false,
		method: 'POST',
		type: 'POST', // For jQuery < 1.9
		success: function(data){
			//alert(data.datax[0].bt_name);
			var i;
			for(i=0;i<data.datax.length;i++)
			{
				//alert(data.datax[i].bt_name);
						$("#province").append(new Option(data.datax[i].name, data.datax[i].id));

			}
		}
	});
	return false;
	}
	function submitform()
	{
		var data = new FormData();
		var data = new FormData($("#nameform")[0]);
		//data.append('store_name', $("input[name='store_name']").val());
		//jQuery.each(jQuery('#filez')[0].files, function(i, file) {
		//    data.append('file-'+i, file);
		//});
		$.ajax({
			url: 'http://localhost/paykitaz-merchant-api/api/daftar',
			data: data,
			cache: false,
			contentType: false,
			processData: false,
			method: 'POST',
			type: 'POST', // For jQuery < 1.9
			success: function(data){
				//session_id_reg = data.session_id_reg;
				//alert(session_id_reg);
				Keychain.setGenericPassword('session', data.session_id_reg);
				navigation.navigate('Register2');
				
			}
		});
		return false;
	}

const sleep = ms => new Promise(resolve => setTimeout(resolve, ms))

const onSubmit = async values => {
  await sleep(300);
  submitform();
}
export default class Register2Screen extends React.Component {

componentDidMount() {
submit();
$('#province').on('change', function() {
	submit2(this.value);
});
$('#kabupaten').on('change', function() {
	submit3(this.value);
});
$('#kecamatan').on('change', function() {
	submit4(this.value);
});
  }
render(){
return (
  <Styles>
    <ImageBackground
      source={require("../assets/register_2_bg.png")}
    >
    <h1>Daftar Paykitaz Merchant</h1>
    <Form
      onSubmit={onSubmit}
      render={({ handleSubmit, reset, submitting, pristine, values }) => (
        <form onSubmit={handleSubmit} name="nameform" id="nameform">
          <div>
            <label>Alamat</label><br/>
			<Field name="merchant_address" component="textarea" placeholder="Notes" />
          </div>
          <div>
            <label>Provinsi</label><br/>
            <Field
              name="province" id="province"
              component="select"
	  onchange="{submit2(this.value)}"
              required
              >
              <option value="0">Select</option>
            </Field>
          </div>
          <div>
            <label>Kabupaten</label><br/>
            <Field
              name="kabupaten" id="kabupaten"
              component="select"
	  onchange="{submit3(this.value)}"
              required
              >
              <option value="0">Select</option>
            </Field>
          </div>
          <div>
            <label>Kota/Kecamatan</label><br/>
            <Field
              name="kecamatan" id="kecamatan"
              component="select"
			  onchange="{submit4(this.value)}"
              required
              >
              <option value="0">Select</option>
            </Field>
          </div>
          <div>
            <label>Kelurahan</label><br/>
            <Field
              name="kelurahan" id="kelurahan"
              component="select"
              required
              >
              <option value="0">Select</option>
            </Field>
          </div>
          <div>
            <label>Kode Pos</label><br/>
            <Field
              name="zip_code"
              component="input"
              type="text"
              placeholder="Kode Pos"
              required
              minLength={4}
              tooShort="Mohon Masukkan Kode Pos"
            />
          </div>
           <div className="buttons">
            <button type="submit" disabled={submitting}>
              Submit
            </button>
            <button type="button" onClick={reset} disabled={submitting}>
              Reset
            </button>
          </div>
        </form>
      )}
    />
    </ImageBackground>
  </Styles>
)

}
}
