'use strict';

import React, {Component}  from 'react';
import {  ImageBackground,
View, Text, TextInput, TouchableHighlight, Picker} from 'react-native';
import ValidationComponent from 'react-native-form-validator';
import {Dimensions} from 'react-native';
import {Image, StyleSheet} from 'react-native';
import Icon from 'react-native-vector-icons/Ionicons';
import $ from 'jquery';
import { WebView } from 'react-native-webview';

const dimensions = Dimensions.get('window');
const imageHeight = dimensions.height ;
const imageWidth = dimensions.width;

	function submit()
	{
	$.ajax({
		url: 'http://localhost/paykitaz-merchant-api/api/paket/businesstype',
		data: "test=test",
		cache: false,
		contentType: false,
		processData: false,
		method: 'POST',
		type: 'POST', // For jQuery < 1.9
		success: function(data){
			//alert(data.datax[0].bt_name);
			var i;
			for(i=0;i<data.datax.length;i++)
			{
				//alert(data.datax[i].bt_name);
						$("#business_type").append(new Option(data.datax[i].bt_name, data.datax[i].bt_id));

			}
		}
	});
	return false;
	}
	function submitform()
	{
		var data = new FormData();
		var data = new FormData($("#nameform")[0]);
		data.append('store_name', store_name);
		//jQuery.each(jQuery('#filez')[0].files, function(i, file) {
		//    data.append('file-'+i, file);
		//});
		$.ajax({
			url: 'http://localhost/paykitaz-merchant-api/api/daftar',
			data: data,
			cache: false,
			contentType: false,
			processData: false,
			method: 'POST',
			type: 'POST', // For jQuery < 1.9
			success: function(data){
				alert(data);
			}
		});
		return false;
	}
	function insertSelect(item)
	{
		$("#business_type").append(new Option(item.bt_name, item.bt_id));
	}

export default class FormTest extends ValidationComponent {

  constructor(props) {
    super(props);
    this.state = {store_name : "", business_type: "", store_open:"07:00", store_close: "17:00", onoff : false, filez : ""
    ,selectedValue: ''};
this._onPressButton = this._onPressButton.bind(this);
  }

componentDidMount() {
submit();
  }
  _onPressButton() {
    // Call ValidationComponent validate method
    if(this.validate({
      store_name: {minlength:3, maxlength:7, required: true},
      store_open: {required: true},
      store_close: {required: true},
      onoff: {required: true},
	  }))
	  {
	  submitform();
	  this.props.navigation.navigate('Register2');
	  }
  }

  render() {
      return (
    <ImageBackground
      style={styles.background}
      source={require("../assets/register_1_bg.png")}
    >
<form onsubmit="return false;" id="nameform"  method="post" enctype="multipart/form-data">
			<WebView ref={'webview'} source={ require("./css-register-1.html") } />
      <View style={styles.toptitle}>
        <Text style={styles.h1}>Daftar Paykitaz Merchant</Text>
      </View>
        <View style={styles.formreg1}>
	  <View style={{flexDirection:'row', justifyContent:'flex-end'}}>
		<img
      src={require('../assets/toko_logo.png')} width={"80"} height={"60"}
		/>
		<Text style={styles.h1_black}>Profil Merchant</Text>
	  </View>
		<Text style={styles.small}>Beritahu Kami tentang Merchant mu.</Text>
			<Text>
            Nama Merchant
          </Text>
  <TextInput name="store_name" ref="store_name" style={styles.inputarea} onChangeText={(store_name) => this.setState({store_name})} value={this.state.store_name} />
			<Text>
            Bidang Bisnis
          </Text>
<View style={styles.container}>
<select name="business_type" id="business_type">
<option value="0">Select</option>
</select>
      </View>
	  <View style={{flexDirection:'row', justifyContent:'flex-end'}}>
	  Waktu Buka<input type="hour" name="store_open"  />Waktu Tutup<input type="hour" name="store_close"/>
	  </View>

	  <View style={{flexDirection:'row', justifyContent:'flex-end'}}>
		<img
      src={require('../assets/toko_logo.png')} width={"80"} height={"60"}
		/>
		<Text style={styles.h1_black}>Foto Merchant</Text>
	  </View>
		<Text style={styles.small}>Khusus Merchant Online, Anda dapat mengupload screenshot profil sosial media atau lapak online anda.</Text>
		<Text style={styles.small}>Tipe Merchant.</Text>
			
	  <View style={{flexDirection:'row', justifyContent:'flex-end'}}>
	  <input type="radio" name="onoff" value="online"/>Online <input type="radio" name="onoff" value="offline"/>Offline 
	  </View>

		<View style={styles.formfile}>
		<input type="file" name="filez"/>
		<Text style={styles.h3_black}>Upload Foto</Text><br/>
		<Text style={styles.small}>Upload Foto Stok Barang, Toko atau Kegiatan Usaha Anda. Pastikan Foto Terlihat jelas dengan pencahayaan yang baik</Text>
		</View>
          <TouchableHighlight onPress={this._onPressButton}>
            <Text>Submit</Text>
          </TouchableHighlight>

          <Text>
            {this.getErrorMessages()}
          </Text>
        </View>
	</form>
	</ImageBackground>
      );
  }

}

const styles = StyleSheet.create({
  background: {
    flex: 1,
    justifyContent: "flex-start",
    alignItems: "center",
    width: "100%",
  },
  inputarea: {
    width: (imageWidth-100),
	height: 20,
	fontSize:14,
	borderBottomWidth:2,
	paddingBottom:20,
  },
  h1:{
	  fontSize:40,
	  color:"#ffffff",
	  padding:20,
  },
  
  h1_black:{
	  fontSize:30,
	  color:"#000000",
	  padding:10,
  },
  h3_black:{
	  fontSize:23,
	  color:"#000000",
	  padding:10,
  },
  small:
  {
	  fontSize:14,
	  color:"#000000",
	  padding:3,
	  
  },
  formreg1:
  {
	  marginTop:80,
	  padding:20,
	  borderRadius:22,
	  borderWidth:2,
	  borderColor: "#e8e4e3",
	  backgroundColor:"#ffffff",
	  width: (imageWidth-40),
	  fontFamily: "Verdana",
	  
  },
  formfile:
  {
	  marginTop:80,
	  padding:20,
	  borderRadius:22,
	  borderWidth:2,
	  borderColor: "#e8e4e3",
	  backgroundColor:"#ffffff",
	  width: (imageWidth-60),
	  fontFamily: "Verdana",
		flexDirection:'row'
		, justifyContent:'flex-end'	  
  }
});
