import React from 'react'
import { render } from 'react-dom'
import {
  StyleSheet,
  Text,
  View,
  ImageBackground,
  Image,
  ScrollView
} from "react-native";
//import {  } from 'react-native-elements';
import AppButton from "../components/appbutton";

import AsyncStorage from '@react-native-async-storage/async-storage';
import Icon from 'react-native-vector-icons/FontAwesome'
import $ from 'jquery';
import {createBottomTabNavigator} from '@react-navigation/bottom-tabs';
const Tab = createBottomTabNavigator();

import {Container, Footer, Header, Button, Content, Title} from 'native-base';

import {Dimensions} from 'react-native';

import Styles from './MainStyles'
import { Form } from 'react-final-form'
import { Field } from 'react-final-form-html5-validation'


const dimensions = Dimensions.get('window');
const screenHeight = dimensions.height ;
const screenWidth = dimensions.width; 

	function gettipebisnis()
	{
		AsyncStorage.getItem('login_id',(err,result) => {
			data.append('login_id',result);
			console.log("emmm");
			console.log(result);
		});
//		alert(token);
		AsyncStorage.getItem('user_name',(err,result) => {
			data.append('user_name',result);
			console.log("emmm");
			console.log(result);
		});
		AsyncStorage.getItem('token',(err,result) => {
			data.append('token',result);
			console.log("emmm");
			console.log(result);
		});
	$.ajax({
		url: 'http://localhost:19006/api/gettoko',
		data: "test=test",
		cache: false,
		contentType: false,
		processData: false,
		method: 'POST',
		type: 'POST', // For jQuery < 1.9
		success: function(data){
			//alert(data.datax[0].bt_name);
			var i;
			for(i=0;i<data.datax.length;i++)
			{
				//alert(data.datax[i].bt_name);
						$("#business_type").append(new Option(data.datax[i].bt_name, data.datax[i].bt_id));

			}
		}
	});
	return false;
	}


export default class DataScreen extends React.Component
{
constructor(props){
 super(props);
	console.log(this.props);
 console.log(props);
 this.render = this.render.bind(this);
}
	async _secondFunction(props){
	await this.send();
  // now wait for firstFunction to finish...
  // do something else
		console.log(props);
		console.log("test");
		console.log(this.props);
		var x = "";
		var y="";
		x = $("#statushidden").val();
		//alert(x);
		AsyncStorage.getItem('statuslogin',(err,result) => {
			y =result;
			console.log("emmm");
			console.log(result);
		});

		if(x=="not ok" || x == undefined)
		{
			//alert("xxx");
			return false;
		}
		if(y=="0")
		{
			this.NavigateNotActive();
			return false;
		}
		this.props.navigation.navigate("Main");
		console.log(this.props);
		}
	render(props){
		console.log("oyy");
		console.log(props);
		console.log("oyy");
		console.log(this.props);
		return(
